*
{
    margin: 0;
    padding: 0;
    font-family: 'Poppins' ,Sans-Serif;
}
.body
{
    opacity: 1;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-wrap: wrap;
    background: #161623;
    min-height: 100vh;
}
.body section
{
    height: 400px;
    width: 800px;
    position: relative;
}
.body section::before
{
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    height: 100%;
    width: 100%;
    background: linear-gradient(#f00,#f0f);
    clip-path: circle(30% at right 70%);
}
.body section::after
{
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    height: 100%;
    width: 100%;
    background: linear-gradient(#2196f3,#e91e63);
    clip-path: circle(20% at 10% 10%);
}
.container * 
{
    transition: 0.5s;
}
.container
{
    position: relative;
    z-index: 1;
    display: flex;
    justify-content: space-between;
    align-items: center;
    /*flex-direction: column;*/
    /*flex-wrap: wrap;////////////*/
    gap: 0px 40px;
}
.container .card
{
    width: 150px;
    height: 150px;
    border-radius: 50%;
    padding: 0px;
    padding-top: 0px;

    position: relative;
    background: rgba(255 ,255 ,255 ,0.05);
    /*margin: 20px;*/
    box-shadow: 0 15px 35px rgba(0,0,0,0.2);
    display: flex;
    align-items: center;
    flex-direction: column;
    backdrop-filter: blur(10px);
    /**/
    border-left: 1px solid rgba(255,255,255,0.3);
    border-top: 1px solid rgba(255,255,255,0.3);
    perspective: 1000px;
    transform-style: preserve-3d;
    transform: perspective(1000px);

    overflow: hidden;
    opacity: 1;
    transition: 0.5s;
    transition-delay: 5s;
}
.container .card:hover
{
    width: 300px;
    height: 400px;
    padding: 10px;
    padding-top: 30px;
    border-radius: 15px;
    gap: 5px;
    transition-delay: 0s;
}
.container .card .imgBx
{
    position: relative;
    /*top: 0;*/
    /*left: 0;*/
    min-width: 150px;
    min-height: 150px;
    border-radius: 50%;
    overflow: hidden;
    border: 10px solid rgba(0,0,0,0.25);
    transition: 0.5s;
    z-index: 1;
    /**/
    /*transform: translateZ(20px);*/

    transition-delay: 5s;
}
.container .card:hover .imgBx
{
    position: relative;
    width: 150px;
    height: 150px;
    /*border: 10px solid rgba(255,255,255,0.25);*/
    box-shadow: 0 15px 35px rgba(0,0,0,0.2);
    transform: translateZ(100px);
    transition-delay: 0.3s;
}
.container .card  .imgBx img
{
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    object-fit: cover;
}


.container .card .contentBx h3
{
    color: #fff;
    text-transform: uppercase;
    letter-spacing: 2px;
    font-weight: 500;
    font-size: 18px;
    text-align: center;
    margin: 20px 0 10px;
    line-height: 1.1em;
    text-shadow: 0 15px 35px rgba(0,0,0,0.2);
}
.container .card .contentBx h3 span 
{
    font-size: 12px;
    font-weight: 300;
    text-transform: initial;
    text-shadow: 0 15px 35px rgba(0,0,0,0.2);
}
