input:
*
{
    padding: 0;
    margin: 0;
    box-sizing: border-box;
}
.body
{
    opacity: 1;
    min-height: 100vh;
}
.main
{
    position: relative;
    width: 100%;
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
    background: #262626;
}
.magnetic
{
    position: relative;
    color: #00000088;
    height: 250px;
    width: 250px;
    display: flex;
    font-size: 2em;
    font-weight: 800;
    border-radius: 8px;
    text-transform: uppercase;
    letter-spacing: 0.15em;
    align-items: center;
    justify-content: center;
    background: #ff001f;
    transition: 0.5s;
}

let box = document.querySelector(".magnetic")
box.addEventListener("mousemove" , (e)=>{
    let x ,y ,BoxWidth ,BoxHeight ,moveX ,moveY;
    x = e.offsetX;
    y = e.offsetY;
    BoxWidth = box.clientWidth;
    BoxHeight = box.clientHeight;
    moveX = (x - BoxWidth/2);
    moveY = (y - BoxHeight/2);
    box.style.transform = `translateX(${moveX}px) translateY(${moveY}px)`;
}) 
box.addEventListener("mouseout" ,(e)=>{box.style.transform = "" ;})
