.menu
{
    position: relative;
    width: 280px;
    height: 280px;
    display: flex;
    justify-content: center;
    align-items: center;
}
.menu li
{
    position: absolute;
    left: 0;
    list-style: none;
    transition: 0.5s;
    transition-delay: calc(0.04s * var(--i));
    transform-origin: 140px;
    transform: rotate(0deg) translateX(110px);
}
.menu.active li
{
    transform: rotate(calc(360deg / 8 * var(--i))) translateX(0px);
}
.menu li a
{
    display: flex;
    justify-content: center;
    align-items: center;
    width: 60px;
    height: 60px;
    background: #2f363e;
    color: var(--clr);
    border: 2px solid var(--clr);
    border-radius: 50%;
    font-size: 1.5em;
    transform: rotate(calc(360deg / -8 * var(--i))) translateX(0px);
    transition: 0.6s;
}
.menu li a:hover
{
    transition: 0s;
    background: var(--clr);
    color: #333;
    box-shadow: 0 0 10px var(--clr),
    0 0 30px var(--clr),
    0 0 50px var(--clr),
    0 0 70px var(--clr);
}
.menu .toggle2
{
    position: absolute;
    width: 60px;
    height: 60px;
    background: #2f363e;
    border: 2px solid #fff;
    border-radius: 50%;
    display: flex;
    justify-content: center;
    align-items: center;
    cursor: pointer;
    z-index: 1000;
    font-size: 2em;
    transition: transform 1.25s;
}
.menu.active .toggle2
{
    transform: rotate(315deg);
}
.toggle2
{
    color: #fff;
}

let toggle2 = document.querySelector('.toggle2');
let menu = document.querySelector('.menu');
toggle2.onclick = ()=>{menu.classList.toggle('active')}
