.body
{
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 100vh;
    background: #3f3752;
    opacity: 0.2;
}
.conatainer
{
    position: relative;
    width: 1200px;
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 20px;
    row-gap: 60px;
    flex-wrap: wrap;
}
.conatainer .serviceBox
{
    position: relative;
    width: 350px;
    height: 280px;
    background: #51446e;
    border-radius: 20px;
    overflow: none;
    transition: 0.3;
}
.conatainer .serviceBox .icon 
{
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    border-radius: 20px;
    background: var(--i);
    transition: 0.5s;
    display: flex;
    justify-content: center;
    align-items: center;
    z-index: 2;
    transition-delay: 0.25s;
}
.conatainer .serviceBox:hover .icon
{
    top: -80px;
    /*left: calc(50% - 40px);*/
    /*width: 80px;*/
    /*height: 80px;*/
    /*border-radius: 50%;*/
    z-index: 5;
    transition-delay: 0s;
}
.conatainer .serviceBox .icon ion-icon
{
    font-size: 5em;
    color: #fff;
    transition: 0.3s;
    transition-delay: 0.25s;
}
.conatainer .serviceBox:hover .icon ion-icon
{
    font-size: 3em;
    transition-delay: 0s;
}
.conatainer .serviceBox .content 
{
    position: absolute;
    padding: 20px;
    color: #fff;
    width: 350px;
    height: 100%;
    text-align: center;
    /*margin-top: 100px;*/
    bottom: -1;
    border-radius: 20px;
    background: #51446e;
    z-index: 1;
    /*transform: scale(0);*/
    transition: 0.3s;
    transition-delay: 0s;
}
.conatainer .serviceBox .content 
{
    bottom: -50;

}
.conatainer .serviceBox .content p
{
    /*display: none;*/
    text-align: start;
    position: absolute;
    height: 0;
    transform: scale(0);
    transition: 0.3s;
}
.conatainer .serviceBox:hover .content p
{
    /*display: inline;*/
    height: auto;
    bottom: 10;
    transform: scale(1);
}
.conatainer .serviceBox:hover .content 
{
    bottom: -80px;
    /*transform: scale(1);*/
    z-index: 4;
    transition-delay: 0.25s;
}
.conatainer .serviceBox .content h2
{
    margin-top: 10px;
    /*margin-bottom: 5px;*/
    position: absolute;
    bottom: 5;
    transition: 0.3s;
}
.conatainer .serviceBox:hover .content h2
{
    margin-top: 10px;
    bottom: 100px;
    margin-bottom: 5px;
}
.conatainer .serviceBox .content p
{
    font-weight: 300;
    line-height: 1.5em;
}
