input:
@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@300;400;500;600&display=swap');
*
{
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    font-family: 'Poppins', sans-serif;
    scroll-behavior: smooth;
    user-select: none;
}
.body
{
    top:0;
    opacity: 1;
    /*background: #fff;*/
    /*padding: 0;*/
    /*display: inline;*/
}
.body > *
{
    display: inline;
}
section.tab
{
    width: 100%;
    min-height: 100vh;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 8em;
    font-weight: 800;
    color: rgba(0,0,0,0.25);
    text-transform: uppercase;
    background: var(--clr);

}

header.nav
{
    position: absolute;
    top: 0;
    width: 100%;
    background: #333;
    display: flex;
    justify-content: space-between;
    align-items: center;
    overflow: hidden;
    padding: 10px 50px 0;
}
a.logo
{
    position: relative;
    color: #fff;
    font-size: 2em;
    font-weight: 700;
    text-decoration: none;
}
header.nav nav.tab-btn 
{
    display: flex;
    gap: 10px;
}
header.nav nav.tab-btn a 
{
    position: relative;
    text-decoration: none;
    padding: 12px 20px;
    color: #fff;
    font-weight: 500;
}
header.nav nav.tab-btn a.active
{
    background: var(--clr);
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
    box-shadow: 0 5px 0 0 var(--clr);
    color: #333;
}
header.nav nav.tab-btn a.active:before
{
    content: '';
    position: absolute;
    left: -20px;
    bottom: 0;
    width: 20px;
    height: 20px;
    background: #0f0;
    border-bottom-right-radius: 20px;
    box-shadow: 5px 5px 0 5px var(--clr);
}
header.nav nav.tab-btn a.active:before
{
    content: '';
    position: absolute;
    left: -20px;
    bottom: 0;
    width: 20px;
    height: 20px;
    background: transparent;
    border-bottom-right-radius: 20px;
    box-shadow: 5px 5px 0 5px var(--clr);
}
header.nav nav.tab-btn a.active:after
{
    content: '';
    position: absolute;
    right: -20px;
    bottom: 0;
    width: 20px;
    height: 20px;
    background: transparent;
    border-bottom-left-radius: 20px;
    box-shadow: -5px 5px 0 5px var(--clr);
}

let sec = document.querySelectorAll("section.tab")
let links = document.querySelectorAll("nav.tab-btn a")
window.onscroll = null;
// window.onscroll = () =>{
let  window_= document.querySelector('.body > *');
window_.onscroll = () =>{
    sec.forEach( section => {
        let top = window_.scrollTop,// scrollY
            offset = section.offsetTop - 60,
            height = section.offsetHeight,
            id = section.getAttribute("id");

        if(top >= offset && top < offset + height)
        {
            links.forEach(link =>{
                link.classList.remove('active');
                document.querySelector('nav.tab-btn a[href*='+id+']').classList.add('active');
            })
        }
    })
}
