*
{
    font-family: 'Poppins', sans-serif;
}
.body 
{
    opacity: 1;
    height: 100vh;
    background: var(--body-color);
    transition: var(--tran-04);
}
:root
{
    --body-color: #e4e9f7;
    --sidebar-color: #fff;
    --primary-color: #695cfe;
    --primary-color-light: #f6f5ff;
    --toggle-color: #ddd;
    --text-color: #707070;

    --tran-02: all 0.2s ease; 
    --tran-03: all 0.3s ease; 
    --tran-04: all 0.4s ease; 
    --tran-05: all 0.5s ease; 
}
.body.dark
{
    --body-color: #18191a;
    --sidebar-color: #242526;
    --primary-color: #3a3b3c;
    --primary-color-light: #3a3b3c;
    --toggle-color: #fff;
    --text-color: #ccc;
}
.sidebar
{
    /*position: absolute;*/
    top: 0;
    left: 0;
    height: 100%;
    width: 250px;
    padding: 10px 14px;
    background: var(--sidebar-color);
    transition: var(--tran-05);
}
.sidebar.close
{
    width: 88px;
    /*overflow: hidden;*/
}

.sidebar .text 
{
    font-size: 16px;
    font-weight: 500;
    color: var(--text-color);
    transition: var(--tran-02);
    white-space: nowrap;
    opacity: 1;
}
.sidebar.close .text 
{
    opacity: 0;
}
.sidebar .image i ion-icon
{
    min-width: 40px;
    display: flex;
    align-items: center;
    justify-content: center;
}
.sidebar li 
{
    height: 50px;
    /*background: red;*/
    margin-top: 10px;
    list-style: none;
    display: flex;
    align-items: center;

}
.sidebar li i.icon ion-icon 
{
    display: flex;
    align-items: center;
    justify-content: center;
    min-width: 60px;
    font-size: 20px;
}
.sidebar li i.icon ion-icon ,
.sidebar li span.text 
{
    color: var(--text-color);
    transition: var(--tran-02);
}
.sidebar header 
{
    position: relative;
}
.sidebar .image-text i ion-icon
{
    width: 40px;
    height: 40px;
    padding: 10px;
    /*margin-bottom: 50px;*/
    font-size: 1.8em;
    background: #f56688;
    border-radius: 6px;
}
.sidebar header .image-text
{
    display: flex;
    align-items: center;
    gap: 5px;
}
header .image-text .header-text
{
    display: flex;
    flex-direction: column;
}
.header-text .name
{
    font-weight: 600;

}
.header-text .profession
{
    margin-top: -2px;
    font-weight: 500;

}
nav.sidebar header span.toggle ion-icon
{
    position: absolute;
    top: 50%;
    right: -25px;
    transform: translateY(-50%)  rotate(180deg);
    height: 25px;
    width: 25px;
    background: var(--primary-color);
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 50%;
    color: var(--sidebar-color);
    transition: var(--tran-05);
}
.body.dark nav.sidebar header span.toggle ion-icon
{
    /*transform: rotate(180deg);*/
    color: var(--text-color);
}
nav.sidebar.close header span.toggle ion-icon
{
    transform: translateY(-50%);
}
.sidebar .menu
{
    margin-top: 35px;
}
.sidebar .search-box
{
    background: var(--primary-color-light);
    border-radius: 6px;
    transition: var(--tran-05);
}
.search-box input
{
    height: 100%;
    width: 100%;
    outline: none;
    border: none;
    font-size: 16px;
    font-weight: 500;
    color: var(--text-color);
    border-radius: 6px;
    background: var(--primary-color-light);
}
.sidebar li a 
{
    height: 100%;
    width: 100%;
    display: flex;
    align-items: center;
    border-radius: 6px;
    text-decoration: none;
    transition: var(--tran-04);
}
.sidebar li a:hover
{
    background: var(--primary-color);
}
.sidebar li a:hover i.icon ion-icon,
.sidebar li a:hover .text 
{
    color: var(--sidebar-color);
}
.body.dark .sidebar li a:hover i.icon ion-icon,
.body.dark .sidebar li a:hover .text 
{
    color: var(--text-color);
}
.sidebar .menu-bar
{
    height: calc(100% - 50px);
    display: flex;
    flex-direction: column;
    justify-content: space-between;
}

.menu-bar .mode
{
    position: relative;
    border-radius: 6px;
    background: var(--primary-color-light);
}
.menu-bar .mode .moon-sun
{
    width: 60px;
    height: 50px;
    display: flex;
    align-items: center;

}
.menu-bar .mode i
{
    position: absolute;
    transition: var(--tran-03);
}
.menu-bar .mode i.sun
{
    opacity: 0;
}
.body.dark .menu-bar .mode i.sun
{
    opacity: 1;
}
.body.dark .menu-bar .mode i.moon
{
    opacity: 0;
}
.menu-bar .mode .toggle-switch
{
    position: absolute;
    right: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    height: 100%;
    min-width: 60px;
    border-radius: 6px;
    cursor: pointer;
    background: var(--primary-color-light);
    /*background: red;*/
}
.toggle-switch .switch
{
    position: relative;
    height: 22px;
    width: 44px;
    border-radius: 25px;
    background: var(--text-color);
}
.switch::before
{
    content: '';
    position: absolute;
    height: 15px;
    width: 15px;
    border-radius: 50%;
    top: 50%;
    left: 5px;
    transform: translateY(-50%);
    background: var(--sidebar-color);
    transition: var(--tran-03);
}
.body.dark .switch::before 
{
    left: 25px;
}

const body = document.querySelector(".body") ,
    sidbar = body.querySelector(".sidebar") ,
    toggle = body.querySelector(".toggle") ,
    searchBox = body.querySelector(".search-box") ,
    modeSwtich = body.querySelector(".toggle-switch") ,
    modeText = body.querySelector(".mode-text") ;

    modeSwtich.addEventListener("click" ,()=>{
        body.classList.toggle("dark");

        if(body.classList.contains("dark"))
        {
            modeText.innerText = "Light Mode";
        }
        else
        {
            modeText.innerText = "Dark Mode";
        }
    })

    toggle.addEventListener("click" ,()=>{
        sidbar.classList.toggle("close");
    })

    searchBox.addEventListener("click" ,()=>{
        sidbar.classList.remove("close");
    })
